function truncateAfterWords($string, $numWords) {\n    $words = explode(' ', $string);\n    if (count($words) > $numWords) {\n        // Get the first `numWords` words and join them with spaces\n        return implode(' ', array_slice($words, 0, $numWords)) . '...';\n    }\n    return $string; // Return original string if it has less than or equal to `numWords`\n}\n\n// Example usage\n$text = "This is an example string that will be truncated after twenty words. Here are some extra words to demonstrate the function.";\n$truncatedText = truncateAfterWords($text, 20);\necho $truncatedText;