<?php\n$imagePath = 'images/someImage.jpg';\n\nif (file_exists($imagePath)) {\n    header('Content-Type: image/jpeg');\n    // Set expiration headers\n    $off = 60 * 60 * 24 * 14; // Two weeks cache time\n    $ExpStr = "Expires: " . gmdate("D, d M Y H:i:s", time() + $off) . " GMT";\n    header($ExpStr);\n    header('Cache-Control: public');\n    readfile($imagePath);\n    exit;\n} else {\n   header('HTTP/1.0 404 Not Found');\n}\n?>