<?php\nrequire 'vendor/autoload.php'; // Make sure to install via Composer\n\nuse SendGrid\Mail\From;\nuse SendGrid\Mail\To;\nuse SendGrid\Mail\Subject;\nuse SendGrid\Mail\Content;\nuse SendGrid\Mail\Mail;\n\n$email = new Mail();\n$email->setFrom(new From("web@example.com", "Website Contact Form"));\n$email->addTo(new To("your-email@gmail.com", "Your Name"));\n$email->setSubject(new Subject("New Contact Form Submission"));\n$email->addContent(\n    "text/plain",\n    "User Email: " . htmlspecialchars($_POST['email']) .\n    "\n\nMessage:\n" . htmlspecialchars($_POST['message'])\n);\n\n$sendgrid = new \SendGrid('YOUR_SENDGRID_API_KEY');\ntry {\n    $response = $sendgrid->send($email);\n    print_r($response);\n} catch (Exception $e) {\n    echo 'Caught exception: '. $e->getMessage();\n}\n?>