<?php\n$ip = $_SERVER['REMOTE_ADDR'];\n$date = date("j. F Y");\n$time = date("H:i:s");\n\n$file_path = './iplogg.html';\n\n// Read the existing content of the file\n$current_content = file_get_contents($file_path);\n\n// Define the new entry to be inserted\n$new_entry = "<tr><td>{$ip}</td><td>{$date}</td><td>{$time}</td></tr>\n";\n\n// Find the position of the closing </table> tag\n$pos = strrpos($current_content, '</table>');\n\nif ($pos !== false) {\n    // Insert the new entry before the </table> tag\n    $new_content = substr_replace($current_content, $new_entry . '  </tbody>' . PHP_EOL . '</table>', $pos, strlen('</table>'));\n    \n    // Write the modified content back to the file\n    file_put_contents($file_path, $new_content);\n}\n?>