class Database {\n    public function query($sql) {\n        // Your database querying logic here\n    }\n}\n\nclass User {\n    protected $name;\n    protected $id;\n\n    public function __construct(Database $db, $userId = null) {\n        if ($userId !== null) {\n            $result = $db->query("SELECT name FROM users WHERE id=$userId");\n            // Assuming single result and no exception handling\n            $this->name = $result[0]['name'];\n            $this->id = $userId;\n        }\n    }\n\n    public function getName() {\n        return $this->name;\n    }\n}\n\nclass Display {\n    protected $database;\n    protected $framework;\n\n    public function __construct(Database $db) {\n        $this->database = $db;\n        $this->buildPage();\n    }\n\n    protected function buildPage() {\n        $this->framework .= $this->header();\n        $this->framework .= $this->body();\n        $this->framework .= $this->sidebar();\n        $this->framework .= $this->footer();\n    }\n\n    private function header() { /* Header logic */ }\n    private function body() {}\n    private function sidebar() { /* Sidebar logic */ }\n    private function footer() { /* Footer logic */ }\n\n    public function displayPage() {\n        print $this->framework;\n    }\n}\n\nclass IndexPHP extends Display {\n    protected function body() {\n        $user = new User($this->database, 1); // Assuming user ID is known or passed\n        return '<div class="body">Hello ' . htmlspecialchars($user->getName()) . '</div>';\n    }\n}\n\n$db = new Database();\n$page = new IndexPHP($db);\n$page->displayPage();