function set_custom_permalinks() {\n    global $wp_rewrite;\n    \n    // Set the new custom post rewrite rules\n    add_permastruct(\n        'custom_structure',\n        '/parent/child/grandchild/%postname%/%',\n        array('with_front' => false)\n    );\n\n    // Flush the rewrites to clear out old rules and apply new ones\n    $wp_rewrite->flush_rules();\n}\nadd_action('after_switch_theme', 'set_custom_permalinks');