<?php\n\nfunction createController($controllerName) {\n    $dir = 'module/Application/src/Controller';\n    if (!file_exists($dir)) {\n        mkdir($dir, 0777, true);\n    }\n    \n    $template = <<<PHP\n<?php\n\nnamespace Application\Controller;\n\nuse Laminas\Mvc\Controller\AbstractActionController;\nuse Laminas\View\Model\ViewModel;\n\nclass {$controllerName}Controller extends AbstractActionController\n{\n    public function indexAction()\n    {\n        return new ViewModel();\n    }\n}\nPHP;\n\n    file_put_contents($dir . "/{$controllerName}Controller.php", $template);\n    \n    echo "Controller {$controllerName} created successfully.\n";\n}\n\n// Usage: php generate_controller.php MyCustom\nif (isset($argv[1])) {\n    createController(ucfirst($argv[1]));\n} else {\n    echo "Usage: php generate_controller.php <ControllerName>\n";\n}\n?>