<?php\nfunction formatDateForChart($date, $locale) {\n    setlocale(LC_TIME, $locale); // Set the locale for date string parsing\n\n    // Define your expected chart formats\n    $chartFormats = [\n        'en_US' => '%m/%d/%Y', // MM/DD/YYYY\n        'de_DE' => '%d-%m-%Y', // DD-MM-YYYY\n        // Add more locales and their corresponding formats as needed\n    ];\n\n    // Get the format based on locale, default to English if not found\n    $format = isset($chartFormats[$locale]) ? $chartFormats[$locale] : $chartFormats['en_US'];\n\n    // Format date according to the determined format\n    return strftime($format, strtotime($date));\n}\n\n// Example usage:\n$dateString = "2023-10-15";\n$userLocale = 'de_DE'; // This should be dynamically set based on user settings or browser settings\n$formattedDate = formatDateForChart($dateString, $userLocale);\necho $formattedDate; // Outputs: 15-10-2023 for de_DE\n?>