<?php\n$mysqli = new mysqli("localhost", "my_user", "my_password", "world");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n$query = "SELECT 'listings' as source, id, title, date_added FROM listings\n          UNION ALL\n          SELECT 'photos' as source, id, filename AS title, upload_date AS date_added FROM photos\n          UNION ALL\n          SELECT 'comments' as source, id, content AS title, post_time AS date_added FROM comments\n          ORDER BY date_added DESC";\n\nif ($result = $mysqli->query($query)) {\n    while ($row = $result->fetch_assoc()) {\n        if ($row['source'] == 'listings') {\n            echo "<div class='listing'>Listing ID: {$row['id']} - Title: {$row['title']}</div>";\n        } elseif ($row['source'] == 'photos') {\n            echo "<div class='photo'>Photo ID: {$row['id']} - Filename: {$row['title']}</div>";\n        } elseif ($row['source'] == 'comments') {\n            echo "<div class='comment'>Comment ID: {$row['id']} - Content: {$row['title']}</div>";\n        }\n    }\n}\n$mysqli->close();\n?>