<?php\n\n// Base class for all form elements\nabstract class FormElement {\n    protected $type;\n    protected $name;\n    protected $label;\n\n    public function __construct($name, $label) {\n        $this->name = $name;\n        $this->label = $label;\n    }\n\n    abstract public function render();\n}\n\n// Class for text input\nclass TextInput extends FormElement {\n    public function __construct($name, $label) {\n        parent::__construct($name, $label);\n        $this->type = 'text';\n    }\n\n    public function render() : string {\n        return "<label for='{$this->name}'>{$this->label}</label>\n                <input type='text' id='{$this->name}' name='{$this->name}'>";\n    }\n}\n\n// Class for dropdown input\nclass Dropdown extends FormElement {\n    private $options;\n\n    public function __construct($name, $label, array $options) {\n        parent::__construct($name, $label);\n        $this->type = 'select';\n        $this->options = $options;\n    }\n\n    public function render() : string {\n        $optionsHtml = '';\n        foreach ($this->options as $value => $text) {\n            $optionsHtml .= "<option value='$value'>$text</option>";\n        }\n        return "<label for='{$this->name}'>{$this->label}</label>\n                <select id='{$this->name}' name='{$this->name}'>\n                    $optionsHtml\n                </select>";\n    }\n}\n\n// Form class that aggregates form elements\nclass Form {\n    private $elements = [];\n\n    public function addElement(FormElement $element) {\n        $this->elements[] = $element;\n    }\n\n    public function render() : string {\n        $html = "<form method='post'>";\n        foreach ($this->elements as $element) {\n            $html .= $element->render();\n        }\n        $html .= "<input type='submit' value='Submit'>";\n        $html .= "</form>";\n        return $html;\n    }\n}\n\n// Example usage\n$form = new Form();\n$form->addElement(new TextInput('username', 'Username'));\n$form->addElement(new Dropdown('role', 'Role', ['admin' => 'Administrator', 'user' => 'User']));\n\necho $form->render();\n\n?>