<?php\n$string = 'Hello <?php echo "World";?>';\n\n// Regular expression to match PHP code within <?php ... ?> tags\npreg_match_all('/<\?php(.*?)\?>/is', $string, $matches);\n\nif (!empty($matches[1])) {\n    foreach ($matches[1] as $code) {\n        // Evaluate the matched PHP code\n        ob_start();\n        eval('?>' . trim($code));\n        $output = ob_get_clean();\n\n        // Replace the original PHP code with the evaluated output\n        $string = str_replace('<?php' . $code . '?>', $output, $string);\n    }\n}\n\n// Echo the final string with evaluated PHP code\necho $string;\n?>