<?php\n$mystring = "abcóüÚdefå";\n\n// Normalize both the string and the filename to NFC (Normalization Form C)\n$normalizedString = normalizer_normalize($mystring, Normalizer::FORM_C);\n\nfile_put_contents($normalizedString, "");\n\n$h = dir('.');\n$h->read(); // "."\n$h->read(); // ".."\n$filename = $h->read();\n\n// Normalize the filename read from the directory\n$normalizedFilename = normalizer_normalize($filename, Normalizer::FORM_C);\n\nprint "string: $mystring and filename: $filename are ";\n\nif ($normalizedString === $normalizedFilename) print "equal\n";\nelse print "different\n";\n?>