<?php\nfunction categorizeMimeType($mimeType) {\n    $mimeCategories = [\n        'office' => [\n            'application/msword',\n            'application/vnd.openxmlformats-officedocument.wordprocessingml.document',\n            'application/vnd.oasis.opendocument.text',\n            'application/pdf'\n        ],\n        'image' => [\n            'image/jpeg',\n            'image/png',\n            'image/gif',\n            'image/bmp',\n            'image/webp'\n        ],\n        'application' => [\n            'application/json',\n            'application/zip',\n            'application/xml'\n        ],\n        'compressed' => [\n            'application/gzip',\n            'application/zip',\n            'application/x-tar',\n            'application/rar'\n        ]\n    ];\n\n    foreach ($mimeCategories as $category => $types) {\n        if (in_array($mimeType, $types)) {\n            return $category;\n        }\n    }\n\n    return 'unknown';\n}\n\n// Example usage\n$mimeType = 'application/vnd.oasis.opendocument.text';\n$category = categorizeMimeType($mimeType);\necho "The MIME type {$mimeType} belongs to the category: {$category}\n";\n?>