<?php\n// Define the path to the file above the web root\n$filePath = __DIR__ . '/../config/data.txt';\n\n// Open the file for reading and writing (creates the file if it does not exist)\n$fileHandle = fopen($filePath, 'c+');\n\nif ($fileHandle === false) {\n    die('Error opening the file.');\n}\n\n// Read the current contents of the file\n$currentData = fread($fileHandle, filesize($filePath));\n\n// Modify or add data to the file\n$newData = $currentData . "\nNew line of data added on " . date('Y-m-d H:i:s');\n\n// Move the file pointer back to the beginning of the file\nfseek($fileHandle, 0);\n\n// Write the new data to the file\nfwrite($fileHandle, $newData);\n\n// Truncate the file to the current position to remove any old remaining content\nftruncate($fileHandle, strlen($newData));\n\n// Close the file handle\nfclose($fileHandle);\n?>