function modulename_theme_registry_alter(&$theme_registry) {\n  if (isset($theme_registry['form_element'])) {\n    $theme_registry['form_element']['function'] = 'modulename_form_element';\n  }\n}\n\n/**\n * Custom theme function for form elements.\n */\nfunction modulename_form_element($variables) {\n  $element = &$variables['element'];\n  \n  // Add a custom prefix to the desired field.\n  if ($element['#field_name'] == 'contenttype_fieldname' && isset($element['#field_prefix'])) {\n    $element['#prefix'] .= '<span class="custom-prefix">' . $element['#field_prefix'] . '</span> ';\n  }\n  \n  return theme_form_element($variables);\n}