function deliverFile($filePath, $fileName) {\n    // Check for file existence\n    if (!file_exists($filePath)) {\n        die('The requested file does not exist.');\n    }\n\n    // Get the file size and mime type\n    $fileSize = filesize($filePath);\n    $finfo = finfo_open(FILEINFO_MIME_TYPE); // return mime type ala mimetype extension\n    $type = finfo_file($finfo, $filePath);\n    finfo_close($finfo);\n\n    // Set headers\n    header('Content-Description: File Transfer');\n    header("Content-Type: " . $type);\n    header("Content-Disposition: inline; filename=\"" . basename($fileName) . "\"");\n    header("Expires: 0");\n    header("Cache-Control: no-cache, must-revalidate");\n    header("Pragma: public"); // HTTP/1.0\n    header("Content-Length: " . $fileSize);\n\n    // Clear output buffer\n    ob_clean();\n    flush();\n\n    // Read the file from disk and send it to the browser in chunks.\n    readfile($filePath);\n}\n\n// Usage example\ndeliverFile('/path/to/file.pdf', 'file.pdf');