class Database {\n    private $config = array(\n        'username' => 'someuser',\n        'password' => 'somepassword',\n        'hostname' => 'some_remote_host',\n        'database' => 'a_database'\n    );\n\n    private $connection;\n\n    function __construct() {\n        $this->connect();\n    }\n\n    private function connect() {\n        $db = $this->config;\n        $this->connection = new mysqli($db['hostname'], $db['username'], $db['password'], $db['database']);\n\n        if ($this->connection->connect_error) {\n            die("Cannot connect to database server: " . $this->connection->connect_error);\n        }\n    }\n\n    public function getConnection() {\n        return $this->connection;\n    }\n}\n\n// Usage in another class\nrequire_once('database.php');\n$db = new Database();\n$mysqliConnection = $db->getConnection();\n\n// Use $mysqliConnection to execute queries