$stmt = $pdo->prepare("SELECT site_id, COUNT(*) as total_hits \n                         FROM hits \n                         WHERE timestamp >= NOW() - INTERVAL 1 DAY AND hit_type = 'in'\n                         GROUP BY site_id \n                         ORDER BY total_hits DESC");\n   $stmt->execute();\n   $topList = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n   print_r($topList); // Array of sites with their hit counts