$retrieveQuery = 'SELECT username, firstname, lastname FROM USERS WHERE username = ?';\n\nif ($getRecords = $con->prepare($retrieveQuery)) {\n    $getRecords->bind_param("s", $username);\n\n    $getRecords->execute();        \n    $getRecords->bind_result($username, $firstname, $lastname);\n    \n    if ($getRecords->fetch()) { // Fetch the results\n        echo "<h1>" . htmlspecialchars($username) . "</h1>\n";\n        echo "<p><strong>First Name: </strong>" . htmlspecialchars($firstname) . "\n</p>";\n        echo "<p><strong>Surname: </strong>" . htmlspecialchars($lastname) . "\n</p>";\n    } else {\n        echo "No records found.";\n    }\n} else {\n    print_r($con->error);\n}