// Load nodes of type 'fund'\n$node_ids = \Drupal::entityQuery('node')\n    ->condition('type', 'fund')\n    ->execute();\n\nforeach ($node_ids as $nid) {\n    $node = \Drupal\node\Entity\Node::load($nid);\n    // Assuming the field name is 'field_factsheet'\n    if (!$node->hasField('field_factsheet')) continue;\n\n    foreach ($node->get('field_factsheet') as $file_item) {\n        $file_uri = $file_item->entity->getFileUri();\n        $file_url = file_create_url($file_uri);\n        // Output the file path\n        echo "File path: " . $file_url;\n    }\n}