<?php\n\n$html = <<<'HTML'\n<html>\n  <head></head>\n  <body>\n    <h2>Title1</h2>\n    <p>Paragraph1</p>\n    <p>Paragraph2</p>\n    <h2>Title2</h2>\n    <p>Paragraph3</p>\n  </body>\n</html>\nHTML;\n\n$dom = new DOMDocument();\n@$dom->loadHTML($html); // Suppress warnings due to missing doctype\n$xpath = new DOMXPath($dom);\n\n// Query to select all h2 and p elements in order\n$query = '//h2 | //p';\n$nodes = $xpath->query($query);\n\nforeach ($nodes as $node) {\n    echo trim($node->nodeValue) . "\n"; // Output the text content of each node\n}\n\n?>