<?php\n\nfunction fetchRssFeed($url) {\n    return simplexml_load_file($url);\n}\n\nfunction aggregateFeeds(array $feedsUrl, $tag = '', $limit = 10) {\n    $items = [];\n    \n    foreach ($feedsUrl as $feedUrl) {\n        $feedItems = fetchRssFeed($feedUrl)->xpath('//item');\n        \n        foreach ($feedItems as $item) {\n            $title = (string)$item->title;\n            $link = (string)$item->link;\n            $description = (string)$item->description;\n            $categories = array_map('trim', explode(',', (string)$item->category));\n            \n            if (empty($tag) || in_array($tag, $categories)) {\n                $items[] = [\n                    'title' => $title,\n                    'link' => $link,\n                    'description' => $description\n                ];\n                \n                if (count($items) >= $limit) break;\n            }\n        }\n    }\n\n    return array_slice($items, 0, $limit);\n}\n\nfunction generateStaticHtmlInclude(array $items) {\n    $html = '<ul>';\n    \n    foreach ($items as $item) {\n        $html .= sprintf('<li><a href="%s">%s</a>: %s</li>', htmlspecialchars($item['link']), htmlspecialchars($item['title']), htmlspecialchars($item['description']));\n    }\n    \n    $html .= '</ul>';\n    \n    return $html;\n}\n\n// Configuration\n$feeds = [\n    'http://example.com/rss1.xml',\n    'http://example.com/rss2.xml',\n    'http://example.com/rss3.xml'\n];\n\n// Generate main news section\n$mainItems = aggregateFeeds($feeds);\nfile_put_contents('main_news_include.html', generateStaticHtmlInclude($mainItems));\n\n// Generate call-out section for a specific tag, e.g., "featured"\n$taggedItems = aggregateFeeds($feeds, 'featured');\nfile_put_contents('call_out_section_include.html', generateStaticHtmlInclude($taggedItems));\n\n?>