function isValidUrl($url) {\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_NOBODY, true); // We don't need the body of the file\n    curl_exec($ch);\n    $code = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    curl_close($ch);\n\n    // Check if the HTTP response code is 200 (OK)\n    return ($code == 200);\n}\n\n// Usage example:\n$url = 'http://example.com/file.js';\nif (isValidUrl($url)) {\n    echo "The URL points to a valid file.";\n} else {\n    echo "The URL does not point to a valid file.";\n}