<?php\n// Path to your .cpp file\n$filePath = 'path/to/yourfile.cpp';\n\n// Check if the file exists and is readable\nif (file_exists($filePath) && is_readable($filePath)) {\n    // Open the file for reading\n    $handle = fopen($filePath, "r");\n\n    // Read the contents of the file\n    $contents = fread($handle, filesize($filePath));\n\n    // Close the file handle\n    fclose($handle);\n\n    // Convert special characters to HTML entities to prevent XSS and maintain formatting\n    $formattedContent = htmlspecialchars($contents, ENT_QUOTES, 'UTF-8');\n\n    // Display the contents within <pre> tags to preserve formatting\n    echo "<pre>$formattedContent</pre>";\n} else {\n    echo "File not found or not readable.";\n}\n?>