interface Animal {\n    public function makeSound();\n}\n\nclass Dog implements Animal {\n    public function makeSound() {\n        return "Bark";\n    }\n}\n\nclass Cat implements Animal {\n    public function makeSound() {\n        return "Meow";\n    }\n}\n\nfunction animalSounds(Animal $animal) {\n    echo $animal->makeSound();\n}\n\n$dog = new Dog();\n$cat = new Cat();\n\nanimalSounds($dog); // Outputs: Bark\nanimalSounds($cat); // Outputs: Meow