<?php\n$arr_nav = array(\n    array("id" => "apple", "url" => "apple.html", "name" => "My Apple"),\n    array("id" => "orange", "url" => "orange/oranges.html", "name" => "View All Oranges"),\n    array("id" => "pear", "url" => "pear.html", "name" => "A Pear")\n);\n\n$totalItems = count($arr_nav);\n$index = 0;\n$v_url_root = ""; // Define your URL root here\n\nforeach ($arr_nav as $item) {\n    $class = '';\n    if ($index === 0) {\n        $class = 'first';\n    } elseif ($index === $totalItems - 1) {\n        $class = 'last';\n    }\n\n    echo '<li id="' . htmlspecialchars($item["id"]) . '" class="' . $class . '"><a href="'\n         . htmlspecialchars($v_url_root . $item["url"]) . '" title="'\n         . htmlspecialchars($item["name"]) . '">' . htmlspecialchars($item["name"])\n         . '</a></li>' . "\r\n";\n\n    $index++;\n}\n?>