class User {\n    private $email;\n\n    // Getter for email\n    public function getEmail() {\n        return $this->email;\n    }\n\n    // Setter for email\n    public function setEmail($email) {\n        if (filter_var($email, FILTER_VALIDATE_EMAIL)) {\n            $this->email = $email;\n        } else {\n            throw new InvalidArgumentException('Invalid email format');\n        }\n    }\n}\n\n$user = new User();\n$user->setEmail("example@example.com");\necho $user->getEmail();