<?php\n// Read the raw POST data\n$xmlData = file_get_contents('php://input');\n\n// Check if we have received any data\nif ($xmlData) {\n    // Load the XML data into a SimpleXMLElement object\n    libxml_use_internal_errors(true); // To suppress errors in case of invalid XML\n    $data = simplexml_load_string($xmlData);\n    \n    if ($data === false) {\n        // Handle XML parsing error\n        foreach(libxml_get_errors() as $error) {\n            echo "Parsing error: ", $error->message;\n        }\n    } else {\n        // Process the XML data here\n        echo "Received and parsed XML data successfully.";\n        \n        // Example: Access a specific element\n        if (isset($data->someElement)) {\n            echo "Value of someElement: ", $data->someElement;\n        }\n    }\n} else {\n    http_response_code(400); // Bad Request\n    echo "No data received";\n}\n?>