class DB {\n    private $table;\n    private $where = '';\n    private $limit = '';\n\n    public function users() {\n        return $this->from('users');\n    }\n\n    public function comments() {\n        return $this->from('comments');\n    }\n\n    private function from($tableName) {\n        $this->table = $tableName;\n        // Return this instance to enable chaining\n        return $this;\n    }\n\n    public function id($condition) {\n        $this->where .= " WHERE id $condition";\n        // Return this instance to enable chaining\n        return $this;\n    }\n\n    public function limit($number) {\n        $this->limit = " LIMIT $number";\n        // Return this instance to enable chaining\n        return $this;\n    }\n\n    public function getQuery() {\n        // Construct the query based on the set properties\n        return "SELECT * FROM {$this->table}{$this->where}{$this->limit};";\n    }\n}\n\n// Usage example:\n$DB = new DB();\necho $DB->users()->id(' > 3')->limit(10)->getQuery(); // Outputs: SELECT * FROM users WHERE id > 3 LIMIT 10;\necho $DB->comments()->limit(5)->getQuery(); // Outputs: SELECT * FROM comments LIMIT 5;