<?php\n\nset_time_limit(300);\n\n// Validate and sanitize the URL input\n$remoteFile = filter_var($_GET['url'], FILTER_VALIDATE_URL);\nif (!$remoteFile) {\n    die("Invalid URL provided.");\n}\n\n// Initialize a cURL session\n$ch = curl_init($remoteFile);\n\n// Set options for cURL\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true); // Return the transfer as a string of the return value of curl_exec() instead of outputting it directly.\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true); // Follow redirects\n\n// Execute the request and capture the response into $fileContent\n$fileContent = curl_exec($ch);\n\nif (curl_errno($ch)) {\n    die("Error downloading the file: " . curl_error($ch));\n}\n\n// Close cURL session\ncurl_close($ch);\n\n// Define the path where you want to save the file\n$savePath = 'here2.mp4'; // Make sure this directory is writable\n\n// Save the content to a file\nif (file_put_contents($savePath, $fileContent) === FALSE) {\n    die("Failed to write the file.");\n}\n\necho "File downloaded and saved successfully!";\n?>