<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');\n\n/**\n * Loops Helper\n *\n * @package		CodeIgniter\n * @subpackage	Helpers\n * @category	Loops Helper\n * @author		Your Name\n */\n\nif ( ! function_exists('custom_loop'))\n{\n    /**\n     * Custom Loop Function\n     * \n     * This function loops through an array and performs actions as needed.\n     *\n     * @param   array  $array\n     * @return  void|string\n     */\n    function custom_loop($array)\n    {\n        // Example looping logic\n        $output = '';\n        foreach ($array as $key => $value) {\n            $output .= "Key: " . htmlspecialchars($key) . ", Value: " . htmlspecialchars($value) . "<br>";\n        }\n        return $output;\n    }\n}\n\n// Add more functions as needed...