<?php\n\nfunction haversineDistance($lat1, $lon1, $lat2, $lon2) {\n    // Radius of the Earth in kilometers\n    $earthRadius = 6371;\n\n    // Convert latitude and longitude from degrees to radians\n    $dLat = deg2rad($lat2 - $lat1);\n    $dLon = deg2rad($lon2 - $lon1);\n\n    // Haversine formula\n    $a = sin($dLat / 2) * sin($dLat / 2) + cos(deg2rad($lat1)) * cos(deg2rad($lat2)) * sin($dLon / 2) * sin($dLon / 2);\n    $c = 2 * atan2(sqrt($a), sqrt(1 - $a));\n\n    // Distance in kilometers\n    $distance = $earthRadius * $c;\n\n    // Convert distance to meters\n    return $distance * 1000;\n}\n\n// Example usage:\n$currentLat = 52.2296756; // Your latitude\n$currentLon = 21.0122287; // Your longitude\n\n$places = [\n    ['name' => 'Place A', 'lat' => 52.230094, 'lon' => 21.011512],\n    ['name' => 'Place B', 'lat' => 52.225674, 'lon' => 21.016838]\n];\n\n$nearbyPlaces = [];\nforeach ($places as $place) {\n    $distance = haversineDistance($currentLat, $currentLon, $place['lat'], $place['lon']);\n    if ($distance <= 100) {\n        $nearbyPlaces[] = ['name' => $place['name'], 'distance' => $distance];\n    }\n}\n\nif (!empty($nearbyPlaces)) {\n    echo "You are nearby the following places:\n";\n    foreach ($nearbyPlaces as $nearbyPlace) {\n        echo "- {$nearbyPlace['name']} ({$nearbyPlace['distance']} meters away)\n";\n    }\n} else {\n    echo "No places are within 100 meters of your location.";\n}\n\n?>