<?php\n// Function to send a trackback notification\nfunction sendTrackback($trackback_url, $post_permalink, $title, $excerpt, $blog_name) {\n    // Build post data\n    $data = http_build_query([\n        'url' => $post_permalink,\n        'title' => $title,\n        'excerpt' => substr(strip_tags($excerpt), 0, 250),\n        'blog_name' => $blog_name,\n        'charset' => 'UTF-8'\n    ]);\n\n    // Initialize cURL session\n    $ch = curl_init();\n\n    // Set options\n    curl_setopt_array($ch, array(\n        CURLOPT_URL => $trackback_url,\n        CURLOPT_POST => true,\n        CURLOPT_POSTFIELDS => $data,\n        CURLOPT_RETURNTRANSFER => true\n    ));\n\n    // Execute the request\n    $response = curl_exec($ch);\n\n    // Check for errors\n    if (curl_errno($ch)) {\n        echo 'Error sending trackback: ' . curl_error($ch);\n    } else {\n        echo 'Trackback successfully sent';\n    }\n\n    // Close the cURL session\n    curl_close($ch);\n}\n\n// Example usage when publishing a post\n$post_permalink = "http://yourblog.com/post/1";\n$title = "First Post";\n$excerpt = "This is a test post for our trackback script.";\n$blog_name = "Your Blog Name";\n\nsendTrackback("http://otherblog.com/trackback/1", $post_permalink, $title, $excerpt, $blog_name);\n?>