<?php\n// Directory where your files are stored\n$directory = '/path/to/your/files';\n\n// Name from the database\n$name = 'DFectuoso';\n\n// Open the directory and read its contents\nif (is_dir($directory)) {\n    if ($handle = opendir($directory)) {\n        // Regex pattern to match files starting with the name followed by an underscore\n        $pattern = '/^' . preg_quote($name, '/') . '_/';\n\n        // Loop through the files in the directory\n        while (false !== ($entry = readdir($handle))) {\n            // Check if the file matches the pattern\n            if (preg_match($pattern, $entry)) {\n                // Display a link to the file\n                echo '<a href="' . htmlspecialchars($directory . '/' . $entry) . '">' . htmlspecialchars($entry) . '</a><br>';\n            }\n        }\n        closedir($handle);\n    } else {\n        echo "Failed to open directory.";\n    }\n} else {\n    echo "The specified path is not a valid directory.";\n}\n?>