<?php\n$ar = array(\n    array(1, 1, 7.50, 'Hello'),\n    array(1, 2, 18.90, 'Hello'),\n    array(3, 5, 11.50, 'Hello'),\n    array(2, 4, 15.90, 'Hello')\n);\n\n// Sort the array based on the third column in descending order\nusort($ar, function($a, $b) {\n    return $b[2] - $a[2];\n});\n\n// Extract the two largest values from the sorted array\n$largestValues = array_slice(array_column($ar, 2), 0, 2);\n\n// Print the results\nforeach ($largestValues as $value) {\n    echo "$value\n";\n}\n?>