<?php\n\n$username = "firstleg_floa";\n$password = "**";\n$hostname = "***1";\n$db = "firstleg_bank";\n$guildUser = strtolower('grissiom');\n\n// Create connection using mysqli\n$conn = new mysqli($hostname, $username, $password, $db);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SELECT * FROM Bank WHERE userId = ?";\n$stmt = $conn->prepare($sql);\n\n// Bind the parameter\n$stmt->bind_param("s", $guildUser);\n\n// Execute and fetch results\n$stmt->execute();\n$result = $stmt->get_result();\n\n$i = 0;\n$rsArr = array();\n\nwhile ($row = $result->fetch_assoc()) {\n    $rsArr[$i][0] = $row['userId'];\n    $rsArr[$i][1] = $row['item'];\n    $rsArr[$i][2] = $row['amount'];\n    $rsArr[$i][3] = $row['position'];\n    $i++;\n}\n\n// Close statement and connection\n$stmt->close();\n$conn->close();\n?>\n\n<?php if (isset($rsArr[0])) { ?>\n    ghdfdgdfg <?php echo htmlspecialchars($rsArr[0][1]); ?> ----\n    <?php echo htmlspecialchars($rsArr[0][0]); ?>\n    <table>\n        <tr><td>Item</td><td>Amount</td></tr>\n        <?php for ($x = 0; $x < $i; $x++) { ?>\n            <tr>\n                <td><?php echo htmlspecialchars($rsArr[$x][1]); ?></td>\n                <td><?php echo htmlspecialchars($rsArr[$x][2]); ?></td>\n            </tr>\n        <?php } ?>\n    </table>\n<?php } else { ?>\n    No data found for user: <?php echo htmlspecialchars($guildUser); ?>\n<?php } ?>