<?php\n$file = 'path/to/bigfile.xml';\n$outputFile = fopen('output.txt', 'w');\n\n$reader = new XMLReader();\n$reader->open($file);\n\n// Tag you are interested in\n$desiredTag = 'yourTagName';\n\nwhile ($reader->read()) {\n    if ($reader->nodeType == XMLREADER::ELEMENT && $reader->name == $desiredTag) {\n        // Read the content of the current node and write it to the output file\n        fwrite($outputFile, $reader->readString() . PHP_EOL);\n    }\n}\n\nfclose($outputFile);\n$reader->close();\n?>