<?php\n// Ensure the cookie is accessible across all relevant paths and domains\n$cookieValue = $galleryId; // Replace with the actual gallery ID you want to store\n$domain = "yourdomain.com"; // Replace with your domain without 'http://'\n$path = "/"; // Cookie available throughout the site\n\nsetcookie('GalleryID', $cookieValue, time() + (86400 * 30), $path, $domain); // 86400 = 1 day\n\n// Rest of your script\n?>