<?php\n// Using exec\nexec("C:\\path\\to\\yourfile.bat", $output, $return_var);\n\nif ($return_var === 0) {\n    echo "Batch file executed successfully.";\n} else {\n    echo "Batch file execution failed with return code: $return_var";\n}\n\nprint_r($output); // To see the output of the batch file\n\n// Alternatively using system\nsystem("C:\\path\\to\\yourfile.bat", $return_var);\n\nif ($return_var === 0) {\n    echo "Batch file executed successfully.";\n} else {\n    echo "Batch file execution failed with return code: $return_var";\n}\n?>