function getMixedContent($node) {\n    $output = '';\n    if ($node->hasChildNodes()) {\n        foreach ($node->childNodes as $childNode) {\n            if ($childNode->nodeType === XML_TEXT_NODE && trim($childNode->textContent) !== '') {\n                // Add text nodes to the output\n                $output .= $childNode->textContent;\n            } elseif ($childNode->nodeType === XML_ELEMENT_NODE) {\n                // Optionally, you can handle child elements here if needed\n                // Or just skip them if you only want the text\n            }\n        }\n    }\n    return $output;\n}\n\n// Load the XML string\n$xmlString = '<node>This is<child>blah</child>som<child>foo</child>text</node>';\n$dom = new DOMDocument();\n$dom->loadXML($xmlString);\n$rootNode = $dom->documentElement;\n\n// Get the mixed content of the node\n$mixedContent = getMixedContent($rootNode);\n\necho trim(preg_replace('/\s+/', ' ', $mixedContent)); // Output: "This issomtext"