<?php\n$servername = "localhost";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$ip_address = $_SERVER['REMOTE_ADDR'];\n\n// Insert IP address into the database\n$sql = "INSERT INTO ip_log (ip_address) VALUES ('$ip_address')";\n\nif ($conn->query($sql) === TRUE) {\n    // Fetch last 10 unique IPs\n    $get_recent_ips_sql = "SELECT DISTINCT ip_address FROM ip_log ORDER BY log_time DESC LIMIT 10";\n    $result = $conn->query($get_recent_ips_sql);\n\n    if ($result->num_rows > 0) {\n        echo "<ul>";\n        while($row = $result->fetch_assoc()) {\n            echo "<li>" . htmlspecialchars($row["ip_address"]) . "</li>";\n        }\n        echo "</ul>";\n    } else {\n        echo "No recent IPs found.";\n    }\n} else {\n    echo "Error: " . $sql . "<br>" . $conn->error;\n}\n\n$conn->close();\n?>