class DataSheet {\n    protected $dbAdapter;\n\n    public function __construct(Zend_Db_Adapter_Abstract $dbAdapter) {\n        $this->dbAdapter = $dbAdapter;\n    }\n\n    public function getValue($columnLabel, $rowLabel, $sheetName = 'default', $locale = 'en') {\n        $sql = "\n            SELECT cells.value\n            FROM cells\n            JOIN rows ON cell.row_id = rows.row_id\n            JOIN columns ON cells.column_id = columns.column_id\n            JOIN sheets ON rows.sheet_id = sheets.sheet_id\n            WHERE sheets.name = ? AND sheets.locale = ?\n              AND rows.label = ? AND columns.label = ?\n        ";\n        $result = $this->dbAdapter->fetchOne($sql, array($sheetName, $locale, $rowLabel, $columnLabel));\n        return $result;\n    }\n\n    // Additional methods to insert/update/delete data can be added here\n}