protected function _initRoutes()\n{\n    $frontController = Zend_Controller_Front::getInstance();\n    $router = $frontController->getRouter();\n\n    // Add a new route that matches your desired URL structure\n    $route = new Zend_Controller_Router_Route(\n        'controller_name/method_name/:param_A/:param_B/:param_C',\n        array(\n            'module' => 'default', \n            'controller' => 'mycontroller', \n            'action' => 'method-name'\n        )\n    );\n\n    $router->addRoute('custom_route', $route);\n}