<?php\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') '\n            . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("CALL exampleProcedure(?, ?, ?)");\n$stmt->bind_param("isn", $param1, $param2, $param3);\n\n$param1 = 5; // or NULL to use default\n$param2 = "someValue"; // or NULL to use default\n$param3 = null; // this will use the current date as default\n\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    echo "Param1: " . $row['param1'] . ", Param2: " . $row['param2'] . ", Param3: " . $row['param3'] . "\n";\n}\n\n$stmt->close();\n$mysqli->close();\n?>