function findTopParent($id) {\n    $page = $this->retrieve("id = '$id'");\n    \n    // Base case: if the parent is 0, this is the topmost page\n    if ($page->parent == 0) {\n        return $page;\n    } else {\n        // Recursive call to find the topmost parent\n        return $this->findTopParent($page->parent);\n    }\n}\n\nfunction topPg() {\n    return $this->findTopParent($this->id); // Assuming $this->id is the starting point\n}