// src/Routing/RouteSubscriber.php\nnamespace Drupal\mymodule\Routing;\n\nuse Symfony\Component\Routing\RouteCollection;\nuse Drupal\Core\Routing\RouteSubscriberBase;\nuse Symfony\Component\Routing\Route;\n\n/**\n * Listens to the dynamic route events.\n */\nclass RouteSubscriber extends RouteSubscriberBase {\n\n  /**\n   * {@inheritdoc}\n   */\n  protected function alterRoutes(RouteCollection $collection) {\n    // Add new route that renders a view without full page template.\n    $route = (new Route('/view/{id}/raw'))\n      ->setDefaults([\n        '_controller' => '\Drupal\mymodule\Controller\CustomController::renderView',\n      ])\n      ->setRequirements(['_permission' => 'access content']);\n    $collection->add('mymodule.route', $route);\n  }\n}