function online($online) {\n    // Debugging: Check values being used for comparison\n    error_log('Current online status: ' . $online);\n    error_log('User level: ' . $_SESSION['exp_user']['userlevel']);\n    \n    if ($online === "0") {  // Ensure strict comparison\n        if ($_SESSION['exp_user']['userlevel'] !== "1") {  // Ensure strict comparison\n            include("error/offline.php");\n            exit();\n        }\n    }\n}