function haversineDistance($lat1, $lon1, $lat2, $lon2) {\n    // Radius of the Earth in miles\n    $R = 3958.8;\n\n    // Convert latitude and longitude from degrees to radians\n    $lat1 = deg2rad($lat1);\n    $lon1 = deg2rad($lon1);\n    $lat2 = deg2rad($lat2);\n    $lon2 = deg2rad($lon2);\n\n    // Calculate the differences between the coordinates\n    $dLat = $lat2 - $lat1;\n    $dLon = $lon2 - $lon1;\n\n    // Apply the Haversine formula\n    $a = sin($dLat / 2) * sin($dLat / 2) +\n         cos($lat1) * cos($lat2) *\n         sin($dLon / 2) * sin($dLon / 2);\n    $c = 2 * atan2(sqrt($a), sqrt(1 - $a));\n\n    // Calculate the distance\n    $distance = $R * $c;\n\n    return $distance;\n}\n\n// Example usage:\n$lat1 = 34.05223; // Latitude of Los Angeles\n$lon1 = -118.24368; // Longitude of Los Angeles\n$lat2 = 40.71278; // Latitude of New York\n$lon2 = -74.00594; // Longitude of New York\n\necho haversineDistance($lat1, $lon1, $lat2, $lon2) . " miles\n";