register_activation_hook(__FILE__, 'bot_install');\n\nfunction bot_install() {\n    global $wpdb;\n    $table = $wpdb->prefix . "sh_gloss";\n\n    // Debug: Log the table name being created\n    error_log("Attempting to create table: " . $table);\n\n    $structure = "CREATE TABLE {$table} (\n        id INT(9) NOT NULL AUTO_INCREMENT,\n        phrase VARCHAR(80) NOT NULL,\n        desc VARCHAR(255) NOT NULL,\n        UNIQUE KEY id (id)\n    );";\n\n    // Execute the SQL and check for errors\n    if (!$wpdb->query($structure)) {\n        error_log("Failed to create table. Error: " . $wpdb->last_error);\n        return;\n    }\n\n    // Debug: Log successful table creation\n    error_log("Table created successfully.");\n\n    // Populate table with test data\n    $insert_query = $wpdb->prepare(\n        "INSERT INTO {$table} (phrase, desc) VALUES (%s, %s)",\n        'Scott Herbert',\n        'Rockstar Programmer'\n    );\n\n    if (!$wpdb->query($insert_query)) {\n        error_log("Failed to insert test data. Error: " . $wpdb->last_error);\n    } else {\n        error_log("Test data inserted successfully.");\n    }\n}