<?php\nfunction cssToArray($cssContent) {\n    $result = [];\n    preg_match_all('/([#.\w\s\[\],>~:()=^$*+\"\']+)\s*\{([^}]*)\}/', $cssContent, $matches);\n\n    foreach ($matches[0] as $i => $match) {\n        $selector = trim($matches[1][$i]);\n        $propertiesText = trim($matches[2][$i]);\n\n        // Split properties by ;\n        $propertiesArray = explode(';', $propertiesText);\n\n        $result[$selector] = [];\n        foreach ($propertiesArray as $property) {\n            if (trim($property) !== '') {\n                list($key, $value) = explode(':', $property, 2);\n                $key = trim($key);\n                $value = trim($value, ' ;');\n                // Store each style rule in its own associative array\n                $result[$selector][] = [$key => $value];\n            }\n        }\n    }\n\n    return $result;\n}\n\n// Example usage:\n$cssContent = "#selector{ display:block; width:100px; } #selector a{ float:left; text-decoration:none; }";\n$array = cssToArray($cssContent);\nprint_r($array);\n?>